import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-category-detail',
  templateUrl: './category-detail.component.html',
  styleUrls: ['./category-detail.component.scss']
})
export class CategoryDetailComponent implements OnInit {
  categoryId: number = 0;
  category: any = {};
  description = '';
  amount = 0;

  constructor(private route: ActivatedRoute, private apiService: ApiService) {}

  ngOnInit() {
    this.categoryId = this.route.snapshot.params['id'];
    this.apiService.getCategoryDetails(this.categoryId).subscribe(
      response => {
        this.category = response;
      },
      error => {
        // Manejo de errores
      }
    );
  }

  addTransaction() {
    this.apiService.createTransaction({
      FCDESCRIPTION: this.description,
      FIAMOUNT: this.amount,
      FIIDCATEGORIE: this.categoryId,
    }).subscribe(
      response => {
        // Transacción agregada exitosamente
        this.description = ''; // Limpiar campos
        this.amount = 0;
      },
      error => {
        // Manejo de errores
      }
    );
  }
}
