import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-category-list',
  templateUrl: './category-list.component.html',
  styleUrls: ['./category-list.component.scss']
})
export class CategoryListComponent implements OnInit {
  categories: any[] = [];

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.loadCategories();
  }

  loadCategories() {
    this.apiService.getCategories().subscribe(
      response => {
        this.categories = response;
      },
      error => {
        // Manejo de errores
      }
    );
  }

  calculateTotalSpent(category: any): number {
    this.apiService.gettransactions().subscribe(
      response => {
        this.categories = response;
      },
      error => {
        // Manejo de errores
      }
    );
    if (category.transactions && category.transactions.length > 0) {
      const totalSpent = category.transactions.reduce((total, transaction) => total + transaction.amount, 0);
      return totalSpent;
    } else {
      return 0; // Si no hay transacciones, el total gastado es 0
    }
  }
}
